virtualIdentityExtension.Log.debug("rdfAccountMismatchDialog.xml mismatchID-menupopup: _addIdentitiesWithSmtp.");
Components.utils.import("resource://v_identity/vI_accountUtils.js", virtualIdentityExtension);
var accounts = virtualIdentityExtension.getAccountsArray();
for (let acc = 0; acc < accounts.length; acc++) {
    let account = accounts[acc];
    let identities = virtualIdentityExtension.getIdentitiesArray(account);
    for (let i = 0; i < identities.length; i++) {
        let identity = identities[i];
        const XUL_NS = "http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul";
        var menuitem = document.createElementNS(XUL_NS, "menuitem"); // create a new XUL menuitem
        menuitem.setAttribute("class", "mismatchMenuitem");
        menuitem.setAttribute("label",  identity.identityName);

        ChromeUtils.import("resource:///modules/mailServices.js");
        var servers = MailServices.smtp.servers;

        var smtpName;
        while (servers && servers.hasMoreElements()) {
          var server = servers.getNext();
          if (server instanceof Components.interfaces.nsISmtpServer &&
            !server.redirectorType && identity.smtpServerKey == server.key) {
            smtpName = server.description ? server.description : server.hostname;
            break;
          }
        }

        menuitem.setAttribute("ext1",  smtpName);
        menuitem.setAttribute("key",  identity.key);
        this.appendChild(menuitem);
    }
}
virtualIdentityExtension.Log.debug("rdfAccountMismatchDialog.xml mismatchID-menupopup: _addIdentitiesWithSmtp done.");

implementation.constructor:

//virtualIdentityExtension.Log.debug("rdfAccountMismatchDialog.xml mismatchID-menupopup: constructor.");
const XUL_NS = "http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul";
this.appendChild(document.createElementNS(XUL_NS, "menuseparator")); // create a new XUL menuseparator
switch (this.getAttribute("type")) {
  case "identity":
    this._addIdentities();
    break;
  case "smtp":
    this._addSmtpServers();
    break;
  case "smtpid":
    this._addIdentitiesWithSmtp();
    break;
}
